const Player = ({ currentSong, audioRef, likedSongs, toggleLike, isPlaying, setIsPlaying }) => {
    return (
        <div style={styles.playerContainer}>
            {currentSong && (
                <>
                    <div style={styles.playerDetails}>
                        <img src={currentSong.image} alt={currentSong.title} style={styles.songCover} />
                        <div style={styles.songInfo}>
                            <h3 style={styles.songTitle}>{currentSong.title}</h3>
                            <p style={styles.songArtist}>{currentSong.artist}</p>
                        </div>
                    </div>
                    <audio ref={audioRef}>
                        <source src={currentSong.audioSrc} type="audio/mp3" />
                    </audio>
                    <div style={styles.playerActions}>
                        <button onClick={() => setIsPlaying(!isPlaying)} style={styles.playButton}>
                            {isPlaying ? <FaPause /> : <FaPlay />}
                        </button>
                        <button onClick={() => toggleLike(currentSong.id)} style={styles.likeButton}>
                            {likedSongs.includes(currentSong.id) ? <FaHeart color="red" /> : <FaRegHeart />}
                        </button>
                    </div>
                </>
            )}
        </div>
    );
};

